def candidate_func(data):
    lines = data.strip().split('\n')
    N = int(lines[0])
    S = lines[1]
    T = lines[2]

    if S == T:
        return "0"

    mapping = {}
    reverse_mapping = {}

    for s, t in zip(S, T):
        if s in mapping:
            if mapping[s] != t:
                return "-1"
        else:
            mapping[s] = t

    for s, t in zip(S, T):
        if t in reverse_mapping:
            if reverse_mapping[t] != s:
                return "-1"
        else:
            reverse_mapping[t] = s

    unique_chars = set(S)
    operations = 0

    for s in unique_chars:
        if s != mapping[s]:
            operations += 1

    return str(operations)
