def candidate_func(s):
    lines = s.split('\n')
    N = int(lines[0])
    A = list(map(int, lines[1].split()))
    stack = []
    max_sum = 0
    current_sum = 0
    for num in A:
        if not stack or num > -stack[-1]:
            stack.append(num)
            current_sum += num
        else:
            current_sum += stack.pop()
        if current_sum > max_sum:
            max_sum = current_sum
    return str(max_sum)
