def candidate_func(s):
    lines = s.strip().split('\n')
    T = int(lines[0])
    index = 1
    results = []
    for _ in range(T):
        N = int(lines[index])
        A = list(map(int, lines[index+1].split()))
        index += 2
        operations = 0
        i = 0
        while i < N:
            if A[i] != A[i+1] if i+1 < N else False:
                operations += 1
                i += 1
            else:
                j = i
                while j < N and A[j] == A[i]:
                    j += 1
                operations += 1
                i = j
        results.append(str(operations))
    return '\n'.join(results)
