def candidate_func(s):
    lines = s.split('\n')
    N = int(lines[0])
    A = list(map(int, lines[1].split()))
    if N == 2:
        return str(abs(A[0] - A[1]))
    total = 0
    while len(A) > 1:
        if len(A) % 2 == 1:
            min_diff = float('inf')
            for i in range(len(A) - 1):
                diff = abs(A[i] - A[i + 1])
                if diff < min_diff:
                    min_diff = diff
                    idx = i
            total += min_diff
            A.pop(idx)
        else:
            max_diff = -float('inf')
            for i in range(len(A) - 1):
                diff = abs(A[i] - A[i + 1])
                if diff > max_diff:
                    max_diff = diff
                    idx = i
            total += max_diff
            A.pop(idx)
            A.pop(idx)
    return str(total)
