import heapq
from collections import defaultdict

def minMaxWeight(n, edges, threshold):
    graph = defaultdict(list)
    for u, v, w in edges:
        graph[u].append((v, w))

    def canAchieve(maxWeight):
        adj = defaultdict(list)
        out_degree = [0] * n

        for u, v, w in edges:
            if w <= maxWeight:
                adj[u].append(v)
                out_degree[u] += 1

        if any(out_degree[i] > threshold for i in range(n)):
            return False

        visited = [False] * n
        stack = [0]
        visited[0] = True
        count = 1

        while stack:
            node = stack.pop()
            for neighbor in adj[node]:
                if not visited[neighbor]:
                    visited[neighbor] = True
                    stack.append(neighbor)
                    count += 1

        return count == n

    weights = sorted(set(w for _, _, w in edges))
    left, right = 0, len(weights) - 1
    result = -1

    while left <= right:
        mid = (left + right) // 2
        if canAchieve(weights[mid]):
            result = weights[mid]
            right = mid - 1
        else:
            left = mid + 1

    return result
