def countMentions(numberOfUsers, events):
    from collections import defaultdict
    import heapq

    # Sort events by timestamp
    events.sort(key=lambda x: int(x[1]))

    # Track offline users with their offline end time
    offline_heap = []  # (end_time, user_id)
    offline_set = set()

    # Initialize mentions count for each user
    mentions = [0] * numberOfUsers

    # Process events in order
    for event in events:
        event_type, timestamp_str, data = event
        timestamp = int(timestamp_str)

        # Process all offline events that end at or before current timestamp
        while offline_heap and offline_heap[0][0] <= timestamp:
            end_time, user_id = heapq.heappop(offline_heap)
            offline_set.discard(user_id)

        if event_type == "OFFLINE":
            user_id = int(data)
            end_time = timestamp + 60
            heapq.heappush(offline_heap, (end_time, user_id))
            offline_set.add(user_id)

        elif event_type == "MESSAGE":
            if data == "ALL":
                # Mention all users, including offline ones
                for i in range(numberOfUsers):
                    mentions[i] += 1
            elif data == "HERE":
                # Mention only online users
                for i in range(numberOfUsers):
                    if i not in offline_set:
                        mentions[i] += 1
            else:
                # Parse individual user mentions
                tokens = data.split()
                for token in tokens:
                    if token.startswith("id"):
                        user_id = int(token[2:])
                        mentions[user_id] += 1

    return mentions
