def maxFreeTime(eventTime, k, startTime, endTime):
    n = len(startTime)
    gaps = []
    for i in range(n - 1):
        gaps.append(startTime[i + 1] - endTime[i])
    gaps.append(eventTime - endTime[-1])
    gaps.insert(0, startTime[0])

    prefix_sum = [0] * (n + 1)
    for i in range(n + 1):
        prefix_sum[i] = prefix_sum[i - 1] + gaps[i] if i > 0 else gaps[i]

    dp = [[0] * (k + 1) for _ in range(n + 1)]

    for i in range(1, n + 1):
        for j in range(k + 1):
            dp[i][j] = dp[i - 1][j]
            for prev in range(i):
                cost = i - prev - 1
                if cost <= j:
                    total_gap = prefix_sum[i - 1] - (prefix_sum[prev - 1] if prev > 0 else 0)
                    dp[i][j] = max(dp[i][j], dp[prev][j - cost] + total_gap)

    return dp[n][k]
