def minCost(nums):
    nums.sort()
    n = len(nums)
    dp = [float('inf')] * (n + 1)
    dp[0] = 0

    for i in range(1, n + 1):
        if i >= 2:
            dp[i] = min(dp[i], dp[i - 2] + max(nums[i - 2], nums[i - 1]))
        if i >= 3:
            dp[i] = min(dp[i], dp[i - 3] + max(nums[i - 3], nums[i - 2], nums[i - 1]))

    return dp[n]
