def candidate_func(input_str):
    MOD = 998244353

    W, H, L, R, D, U = map(int, input_str.strip().split())

    fact = [1] * (W + H + 2)
    for i in range(1, W + H + 2):
        fact[i] = (fact[i-1] * i) % MOD

    def modinv(a):
        return pow(a, MOD - 2, MOD)

    def comb(n, r):
        if r < 0 or r > n:
            return 0
        return (fact[n] * modinv(fact[r]) % MOD) * modinv(fact[n - r]) % MOD

    total = comb(W + H, W)

    if L <= R and D <= U:
        bottom_left = comb(L + D, L)
        top_right = comb((W - R) + (H - U), W - R)
        subtract = (bottom_left * top_right) % MOD
        total = (total - subtract) % MOD

    return str(total)
