def candidate_func(input_str):
    lines = input_str.strip().split('\n')
    N = int(lines[0])
    A = list(map(int, lines[1].split()))

    # Dynamic programming approach
    # dp[i][j] represents the maximum score for subarray A[i:j+1]
    # We use memoization to avoid recomputation

    from functools import lru_cache

    @lru_cache(maxsize=None)
    def dp(i, j):
        if j - i < 1:
            return 0
        if j - i == 1:
            return abs(A[i] - A[j])

        # Try all possible pairs to remove
        max_score = 0
        # Remove first two elements
        max_score = max(max_score, abs(A[i] - A[i+1]) + dp(i+2, j))
        # Remove last two elements
        max_score = max(max_score, abs(A[j-1] - A[j]) + dp(i, j-2))
        # Remove first and last elements (only if length > 2)
        if j - i > 2:
            max_score = max(max_score, abs(A[i] - A[j]) + dp(i+1, j-1))

        # Try removing middle adjacent pairs
        for k in range(i+1, j):
            score = abs(A[k] - A[k+1]) + dp(i, k-1) + dp(k+2, j)
            max_score = max(max_score, score)

        return max_score

    return str(dp(0, N-1))
