def candidate_func(input_data):
    lines = input_data.strip().split('\n')
    N = int(lines[0])
    S = lines[1]

    MOD = 998244353

    W_positions = []
    B_positions = []
    for i in range(2 * N):
        if S[i] == 'W':
            W_positions.append(i + 1)
        else:
            B_positions.append(i + 1)

    from functools import lru_cache
    import sys
    sys.setrecursionlimit(2 * 10**5 + 100)

    @lru_cache(None)
    def dp(i, j):
        if i == N:
            return 1
        res = 0
        for k in range(j, N):
            w_pos = W_positions[i]
            b_pos = B_positions[k]
            if w_pos < b_pos:
                res = (res + dp(i + 1, k + 1)) % MOD
        return res

    result = dp(0, 0)
    return str(result)
